import React, { useState } from 'react';
import { TextField, Button, Typography, Box, Grid, Paper } from '@mui/material';
import axios from 'axios';

const Otp = () => {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Make an API call to the backend to send OTP to the provided email
      const response = await axios.post('http://localhost:5000/send-otp', { email });
      setMessage('OTP has been sent to your email!');
      setError('');
    } catch (err) {
      setMessage('');
      setError('Error sending OTP. Please try again.');
    }
  };

  return (
    <Grid container justifyContent="center" alignItems="center" style={{ minHeight: '100vh' }}>
      <Paper elevation={3} style={{ padding: '30px', width: '350px', textAlign: 'center' }}>
        <Typography variant="h5" gutterBottom>
          Get OTP via Email
        </Typography>
        <form onSubmit={handleSubmit}>
          <TextField
            label="Enter your email"
            variant="outlined"
            fullWidth
            required
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={{ marginBottom: '20px' }}
          />
          <Button type="submit" variant="contained" color="primary" fullWidth>
            Send OTP
          </Button>
        </form>
        {message && <Typography style={{ color: 'green', marginTop: '20px' }}>{message}</Typography>}
        {error && <Typography style={{ color: 'red', marginTop: '20px' }}>{error}</Typography>}
      </Paper>
    </Grid>
  );
};

export default Otp;
