import React from 'react';
import { TextField, Button, Typography, Box, Grid, Paper } from '@mui/material';

const Otp = (props) => {


 

  return (
    <Grid container justifyContent="center" alignItems="center" style={{ minHeight: '100vh' }}>
      <Paper elevation={3} style={{ padding: '30px', width: '350px', textAlign: 'center' }}>
        <Typography variant="h5" gutterBottom>
          Get OTP via Email
        </Typography>
        <form onSubmit={handleSubmit}>
          <TextField
            label="Enter your email"
            variant="outlined"
            fullWidth
            required
            value={email}
            onChange={(e) => props.setEmail(e.target.value)}
            style={{ marginBottom: '20px' }}
          />
          <Button type="submit" variant="contained" color="primary" fullWidth>
            Send OTP
          </Button>
        </form>
        {props.message && <Typography style={{ color: 'green', marginTop: '20px' }}>{props.message}</Typography>}
        {props.error && <Typography style={{ color: 'red', marginTop: '20px' }}>{props.error}</Typography>}
      </Paper>
    </Grid>
  );
};

export default Otp;
