import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./logout.css";
import axios from 'axios';

export default function Signup(props) {
  const [credentials, setcredential] = useState({
    name: "",
    email: "",
    voterid:"",
    aadharNumber: "",
    password: "",
  });
  const [content, setcontent] = useState("");
  const password = document.querySelector("#exampleInputEmail1");
  const navigate = useNavigate();

  //Otp genrator
  const forotp = async (e) => {
    e.preventDefault();

    try {
      // Make an API call to the backend to send OTP to the provided email
      const response = await axios.post('http://localhost:5000/send-otp', { });
      localStorage.setItem("sentotp","otp");
    } catch (err) {
      console.log("error");
    }
  };

  const handleonClick = async (e) => {
    e.preventDefault();
    const url = "http://localhost:5000/api/auth/createuser";
    const response = await fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: credentials.name,
        voterid:credentials.voterid,
        aadharNumber: credentials.aadharNumber,
        email: credentials.email,
        password: credentials.password,
      }),
    });
    const json = await response.json();
    if (json.success === true) {
      forotp();
      navigate("/otp");
      props.handleonClick2();
    } else {
      props.showalert();
      password.style.border = "1px solid red";
      setcontent("User with this Email is already exist");
    }
  };
  const onchange = async (e) => {
    await setcredential({ ...credentials, [e.target.name]: e.target.value });
    //password.style.border = "";
    setcontent("");
  };
  return (
    <>
      <div className="container1" style={{width:"100%",display:"flex",justifyContent:"center",alignItems:"center",zIndex:"10"}}>
      <div
        className="container"
        style={{
          marginTop: "10px",
          maxWidth: "500px",
          minHeight: "370px",
          border: "2px solid #EAEAEA",
          boxSizing: "border-box",
          paddingTop: "20px",
          borderRadius: "12px",
          boxShadow: "0px 10px 25px #000",
          backgroundColor:"whitesmoke",//#E4F1E8
          opacity:"0.9",
          backgroundImage:"url('Screenshot 2024-10-12 160745.png')",
          backgroundSize:"cover",
          fontWeight:"bolder"
        }}
      >
        <h3 style={{fontFamily:"arial",fontSmooth:"2px",fontWeight:"700",marginBottom:"20px"}}>Create account</h3>
          <form onSubmit={handleonClick}>
            <div className="mb-3 my-4">
              <label htmlFor="name" className="form-label">
                Name
              </label>
              <input
                type="name"
                className="form-control"
                id="name"
                aria-describedby="emailHelp"
                name="name"
                value={credentials.name}
                onChange={onchange}
              />
            </div>
            <div className="mb-3 my-2">
              <label htmlFor="exampleInputEmail1" className="form-label">
                Email address
              </label>
              <input
                type="email"
                className="form-control"
                id="exampleInputEmail1"
                aria-describedby="emailHelp"
                name="email"
                value={credentials.email}
                onChange={onchange}
              />
              <p style={{ color: "red", marginTop: "5px", marginLeft: "2px" }}>
                {content}
              </p>
            </div>
            <div className="mb-3 my-2">
              <label htmlFor="exampleInputvoterid" className="form-label">
                Voter Id
              </label>
              <input
                type="password"
                className="form-control"
                id="exampleInputvoterid"
                name="voterid"
                value={credentials.voterid}
                onChange={onchange}
              />
            </div>
            <div className="mb-3 my-2">
              <label htmlFor="exampleInputaadharNumber1" className="form-label">
                AadharNumber
              </label>
              <input
                type="password"
                className="form-control"
                id="exampleInputaadharNumber1"
                name="aadharNumber"
                value={credentials.aadharNumber}
                onChange={onchange}
              />
            </div>
            <div className="mb-3 my-2">
              <label htmlFor="exampleInputPassword1" className="form-label">
                Password
              </label>
              <input
                type="password"
                className="form-control"
                id="exampleInputPassword1"
                name="password"
                value={credentials.password}
                onChange={onchange}
              />
            </div>
            <button
              type="submit"
              className="same-btn1"
              onClick={handleonClick}
            >
              SignUp
            </button>
            <Link to="/login" style={{ textDecoration: "none" }}>
              <p onClick={props.handleonClick2}>
                {" "}
                Already have an account.. Login
              </p>
            </Link>
          </form>
        </div>
      </div>
    </>
  );
}
